from flask_wtf import FlaskFormfrom wtforms import IntegerField, SubmitField, validators, FloatFieldclass InputForm(FlaskForm):    IntInv = IntegerField('Initial Investment Expenditures (in k):', [validators.DataRequired()], default=1500)    OpMainCosts = IntegerField('Annual Operations & Maintenance Costs (in k):', [validators.DataRequired()], default=100)    OpMainGrowth = FloatField('Annual Operations & Maintenance Costs Growth Rate:', [validators.DataRequired()], default=0.02)    FuelExp = IntegerField('Annual Fuel Expenditures (in k):', [validators.DataRequired()], default=0)    ElectGen = IntegerField('Annual Electrical Energy Generated in MW:', [validators.DataRequired()], default=3)    DiscRate = FloatField('Discount Rate:', [validators.DataRequired()], default=0.08)    ExpLife = IntegerField('Expected Project Lifetime:', [validators.DataRequired()], default=20)    submit = SubmitField('Submit')